import {useState, useEffect} from 'react'







export default function HomePage({onChange, newState}){

    const [nodes, setNodes] = useState(undefined);

    const[postData, setPostData] = useState(undefined);

    if(nodes === undefined){

        fetch(`http://127.0.0.1:5000/getUser/${newState[1]}`, {
            headers : {
                'Content-Type' : 'application/json',
                'Access-Control-Allow-Origin' : '*'
            },
            method : 'GET',
            mode: 'cors',
        })
        .then(res => res.json()).then((data) => {
            setNodes(data)
        }
        )
        .catch(function(error) {
            console.log(error);
        });

    }
    if(postData === undefined){
        fetch('http://127.0.0.1:5000/interactPost', {
            headers : {
                'Content-Type' : 'application/json',
                'Access-Control-Allow-Origin' : '*'
            },
            method : 'GET',
            mode: 'cors',
        })
        .then(function (response){

            if(response.ok) {
                response.json()
                .then(function(response) {
                    setPostData(response)
                });
            }
            else {
                throw Error('Something went wrong');
            }
        })
        .catch(function(error) {
            console.log(error);
        });
    }



    if(nodes === undefined || postData === undefined){
        return (<h1>Yeah you suck</h1>)
    }
    return (

        <>
            <div className='App'>

                <div style={{display: "grid", gridTemplateColumns: "1fr 2fr 1fr"}}>
                    <div></div>
                    <div><h1>Welcome {nodes[0][1]}</h1></div>
                    <div>
                    <button onClick={onChange}>LOG OUT</button>
                    </div>

                </div>


                <div>
                    <div>
                    <label>Post Content: </label>
                    </div>
                    <div>
                    <textarea id="postArea" rows="4" cols="50"></textarea>
                    </div>


                    <button onClick={() => {      
                        postData(nodes)
                    }}>POST</button>
                </div>

            </div>
            <div style={{paddingLeft: 200}}>
                <h1>POSTS: </h1>
                {
                    postData.map(data => {

                        return(
                        <div>
                            <label>{data[5]}: </label>
                            <label>{data[2]}</label>
                        </div>)


                    })
                }
            </div>
        </>

    )
}

async function postData(nodes) {
    fetch('http://127.0.0.1:5000/interactPost', {
        headers : {
            'Content-Type' : 'application/json',
            'Access-Control-Allow-Origin' : '*'
        },
        method : 'POST',
        mode: 'cors',
        body : JSON.stringify( {
            'userid': nodes[0][0],
            'content' : document.getElementById('postArea').value,
            'createdAt': new Date().toLocaleString()
        })
    })
    .then(function (response){

        if(response.ok) {
            response.json()
            .then(function(response) {
                console.log(response);
            });
        }
        else {
            throw Error('Something went wrong');
        }
    })
    .catch(function(error) {
        console.log(error);
    });

}
